import { getProductById } from "../lib/axios";
import axios from "axios";

const express = axios.create({
  baseURL: "http://localhost:3000",
  headers: { "Content-Type": "application/json" },
});

export async function checkout(cart) {
  try {
    const response = await express.post("/charge", {
      body: JSON.stringify({
        cart,
      }),
    });
    return response;
  } catch (error) {
    console.error(error);
  }
}

const Home = () => {
  return (
    <div>
      <h1>Home</h1>
      {/* Feature 3 items here */}
      <div>Item 1</div>
      <div>Item 2</div>
      <div>Item 3</div>
      {/* Branding */}
      <div>Banner</div>

      {/* fetch("/charge", {
        baseUrl: "http://localhost:3000",
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          items: [
            { id: 1, quantity: 3 },
            { id: 2, quantity: 1 },
          ],
        }),
      }) */}
      <button
        onClick={() => {
          checkout()
            .then((res) => {
              if (res.ok) return res.json();
              return res.json().then((json) => Promise.reject(json));
            })
            .then(({ url }) => {
              // window.location = url;
              console.log(url);
            })
            .catch((e) => [console.error(e.error)]);
        }}
      >
        Checkout
      </button>

      {/* Filtered Category Products*/}
    </div>
  );
};

export default Home;
